CREATE TABLE INFO (ID BIGINT AUTO_INCREMENT NOT NULL, DATE DATE, STATUS VARCHAR(255), TIMESTAMP BIGINT, TYPE VARCHAR(255), VALUE VARCHAR(255), ROOM_ID BIGINT, PRIMARY KEY (ID));
CREATE TABLE CATEGORY (ID BIGINT AUTO_INCREMENT NOT NULL, NAME VARCHAR(255), PRIMARY KEY (ID));
CREATE TABLE EXECUTOR (ID BIGINT AUTO_INCREMENT NOT NULL, DESCRIPTION VARCHAR(255), STATUS VARCHAR(255), VALUERANGE VARCHAR(255), VALUETYPE VARCHAR(255), VALUEVOLUME VARCHAR(255), CATEGORY_ID BIGINT, Controller_id BIGINT, PRIMARY KEY (ID));
CREATE TABLE SENSOR (ID BIGINT AUTO_INCREMENT NOT NULL, DESCRIPTION VARCHAR(255), STATUS VARCHAR(255), VALUERANGE VARCHAR(255), VALUETYPE VARCHAR(255), VALUEVOLUME VARCHAR(255), CATEGORY_ID BIGINT, Controller_id BIGINT, PRIMARY KEY (ID));
CREATE TABLE COMMAND (ID BIGINT AUTO_INCREMENT NOT NULL, COMPLETE TINYINT(1) default 0, DATE DATE, STATUS VARCHAR(255), TIMESTAMP BIGINT, TYPE VARCHAR(255), VALUE VARCHAR(255), ROOM_ID BIGINT, USER_ID BIGINT, PRIMARY KEY (ID));
CREATE TABLE MESSAGE (ID BIGINT AUTO_INCREMENT NOT NULL, CONTENT VARCHAR(255), POSTED DATE, ROOM_ID BIGINT, USER_ID BIGINT, PRIMARY KEY (ID));
CREATE TABLE USER (ID BIGINT AUTO_INCREMENT NOT NULL, EMAIL VARCHAR(255), FIRSTNAME VARCHAR(255), IMAGEURL VARCHAR(255), LASTNAME VARCHAR(255), PASSWORD VARCHAR(255), PHONE VARCHAR(255), USERROLE INTEGER, USERSTATUS INTEGER, USERNAME VARCHAR(255), PRIMARY KEY (ID));
CREATE TABLE CONTROLLER (ID BIGINT AUTO_INCREMENT NOT NULL, DESCRIPTION VARCHAR(255), IP VARCHAR(255), STATUS VARCHAR(255), ALTITUDE DOUBLE, LATITUDE DOUBLE, LONGITUDE DOUBLE, PRIMARY KEY (ID));
CREATE TABLE ROOM (ID BIGINT AUTO_INCREMENT NOT NULL, NAME VARCHAR(255), ALTITUDE DOUBLE, LATITUDE DOUBLE, LONGITUDE DOUBLE, CONTROLLERS_ID BIGINT, PRIMARY KEY (ID));
ALTER TABLE INFO ADD CONSTRAINT FK_INFO_ROOM_ID FOREIGN KEY (ROOM_ID) REFERENCES ROOM (ID);
ALTER TABLE EXECUTOR ADD CONSTRAINT FK_EXECUTOR_Controller_id FOREIGN KEY (Controller_id) REFERENCES CONTROLLER (ID);
ALTER TABLE EXECUTOR ADD CONSTRAINT FK_EXECUTOR_CATEGORY_ID FOREIGN KEY (CATEGORY_ID) REFERENCES CATEGORY (ID);
ALTER TABLE SENSOR ADD CONSTRAINT FK_SENSOR_Controller_id FOREIGN KEY (Controller_id) REFERENCES CONTROLLER (ID);
ALTER TABLE SENSOR ADD CONSTRAINT FK_SENSOR_CATEGORY_ID FOREIGN KEY (CATEGORY_ID) REFERENCES CATEGORY (ID);
ALTER TABLE COMMAND ADD CONSTRAINT FK_COMMAND_USER_ID FOREIGN KEY (USER_ID) REFERENCES USER (ID);
ALTER TABLE COMMAND ADD CONSTRAINT FK_COMMAND_ROOM_ID FOREIGN KEY (ROOM_ID) REFERENCES ROOM (ID);
ALTER TABLE MESSAGE ADD CONSTRAINT FK_MESSAGE_ROOM_ID FOREIGN KEY (ROOM_ID) REFERENCES ROOM (ID);
ALTER TABLE MESSAGE ADD CONSTRAINT FK_MESSAGE_USER_ID FOREIGN KEY (USER_ID) REFERENCES USER (ID);
ALTER TABLE ROOM ADD CONSTRAINT FK_ROOM_CONTROLLERS_ID FOREIGN KEY (CONTROLLERS_ID) REFERENCES CONTROLLER (ID);